<script setup lang="ts">
import type { Page } from '~/types';

defineProps<{
  title: string,
}>()

const pages = inject('pages') as Page[]

const menuActive = ref<boolean>(false)

function animateSvg() {
  const svgPaths = document.querySelectorAll('.header__bg svg path')

  svgPaths.forEach(path => {
    path.setAttribute('fill-opacity', String(Math.round(Math.random()) === 0 ? 0.28 : 0))

    setInterval(() => {
      path.setAttribute('fill-opacity', String(Math.round(Math.random()) === 0 ? 0.28 : 0))
    }, 5000)
  })
}

onMounted(() => {
  animateSvg()
})
</script>

