<style scoped lang="scss">
.header {
  position: relative;
  mix-blend-mode: difference;
  z-index: 9;
  
  .container {
    position: relative;
    display: flex;
    align-items: center;
  }

  &__title {
    padding: 1rem 0;
  }

  &__menu {
    position: fixed;
    right: 1rem;
    text-transform: uppercase;
    margin-left: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;

    span {
      transition: transform var(--transition);

      &.active {
        transform: rotate(-180deg) translateY(-2rem);
      }
    }

    &:hover {
      span:not(.active) {
        transform: translate(50%, 25%) rotate(45deg);
      }
    }
  }
  
  &__navigation {
    background: rgba(31, 31, 31, 0.5);
    position: fixed;
    top: 5rem;
    right: 0;
    text-transform: uppercase;
    display: flex;
    flex-direction: column;
    transform: translate(50%);
    opacity: 0;
    visibility: hidden;
    transition: var(--transition);

    a {
      padding: .5rem .5rem .5rem .5rem;
      transition: var(--transition);
      cursor: pointer;

      &:hover {
        background: var(--white);
        color: var(--black);
      }
    }

    &.active {
      transform: translate(0);
      opacity: 1;
      visibility: visible;
    }
  }

  &__bg svg {
    pointer-events: none;
    position: absolute; 
    top: -25%;
    left: -25%;
    height: 150%;
    width: 150%;

    path {
      transition: 5s ease;
    }
  }
}
</style>